package com.werner.jms;import lombok.extern.slf4j.Slf4j;import org.springframework.jms.annotation.JmsListener;import org.springframework.messaging.handler.annotation.SendTo;import org.springframework.stereotype.Component;@Component@Slf4jpublic class Consumer {    @JmsListener(destination = "werner.queue")    @SendTo("werner.received")    public String receiveQueue(String message) {        log.info("消费者 1 收到消息" + message);        return "消费者1" + message;    }    @JmsListener(destination = "werner.queue",containerFactory = "jmsListenerContainerQueue")    @SendTo("werner.received")    public String receiveQueue2(String message) {        log.info("消费者 2 收到消息" + message);        return "消费者2" + message;    }    @JmsListener(destination = "werner.topic",containerFactory = "jmsListenerContainerTopic")    public void receiveTopic(String message) {        log.info("1号员工收到订阅消息" + message);    }    @JmsListener(destination = "werner.topic",containerFactory = "jmsListenerContainerTopic")    public void receiveTopic2(String message) {        log.info("2号员工收到订阅消息" + message);    }}